import React, { useState, useEffect } from 'react';
import { Table, Tag, Button, Modal, Spin, message } from 'antd';
import { getApplications, getAppConfig } from '../api';

export default function Applications() {
    const [apps, setApps] = useState([]);
    const [config, setConfig] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        loadData();
    }, []);

    const loadData = async () => {
        setLoading(true);
        try {
            const { data } = await getApplications();
            setApps(data);
        } catch (err) {
            message.error('Fehler beim Laden der Applikationen');
        } finally {
            setLoading(false);
        }
    };

    const showConfig = async (appKey) => {
        try {
            const { data } = await getAppConfig(appKey);
            setConfig({ appKey, data });
        } catch (err) {
            message.error(`Konfiguration für ${appKey} konnte nicht geladen werden`);
        }
    };

    const columns = [
        {
            title: 'App Key',
            dataIndex: 'key',
            render: (key) => <Tag color="geekblue">{key}</Tag>,
        },
        {
            title: 'Name',
            dataIndex: 'name',
        },
        {
            title: 'Schema',
            dataIndex: 'schema',
        },
        {
            title: 'Status',
            dataIndex: 'active',
            render: (active) => (
                <Tag color={active ? 'green' : 'red'}>
                    {active ? 'AKTIV' : 'INAKTIV'}
                </Tag>
            ),
        },
        {
            title: 'Aktionen',
            render: (_, record) => (
                <Button onClick={() => showConfig(record.key)}>
                    Konfiguration anzeigen
                </Button>
            ),
        },
    ];

    return (
        <div style={{ padding: '24px' }}>
            <Table
                columns={columns}
                dataSource={apps}
                rowKey="id"
                loading={loading}
            />

            <Modal
                title={`Konfiguration: ${config?.appKey || ''}`}
                visible={!!config}
                onCancel={() => setConfig(null)}
                footer={null}
                width={800}
            >
                <pre style={{
                    background: '#1e1e1e',
                    color: '#d4d4d4',
                    padding: '16px',
                    borderRadius: '4px',
                    maxHeight: '60vh',
                    overflow: 'auto'
                }}>
                    {JSON.stringify(config?.data, null, 2)}
                </pre>
            </Modal>
        </div>
    );
}
