import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CarouselMenu from './components/CarouselMenu';
import Applications from './pages/Applications';
import Users from './pages/Users';
import Maintenance from './pages/Maintenance';
import SiteProperties from './pages/SiteProperties';
import './App.css';

function App() {
  return (
    <Router>
      <div className="app-container">
        {/* Klickbarer Titel (immer sichtbar) */}
        <Link to="/" className="app-title">
          <h1>ApplicationManager</h1>
        </Link>

        <Routes>
          {/* Startseite mit Karussell */}
          <Route path="/" element={
            <div className="menu-container">
              <CarouselMenu />
            </div>
          } />

          {/* Einzelne Seiten */}
          <Route path="/applications" element={<Applications />} />
          <Route path="/users" element={<Users />} />
          <Route path="/maintenance" element={<Maintenance />} />
          <Route path="/site-properties" element={<SiteProperties />} />
        </Routes>
      </div>
    </Router>
  );
}



// Stile für den Weltraum-Effekt
const styles = {
  spaceBackground: {
    background: 'radial-gradient(ellipse at center, #0a0e24 0%, #000000 100%)',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white'
  }
};

export default App;
