import React, { useState, useEffect } from 'react';
import { getApplications, getAppConfig } from '../api';
import './Applications.css';

export default function Applications() {
    const [apps, setApps] = useState([]);
    const [selectedAppKey, setSelectedAppKey] = useState(null);
    const [configData, setConfigData] = useState({});
    const [activeTab, setActiveTab] = useState(null);

    useEffect(() => {
        loadApplications();
    }, []);

    const loadApplications = async () => {
        try {
            const { data } = await getApplications();
            setApps(data);
        } catch (err) {
            console.error('Fehler beim Laden:', err);
        }
    };

    const loadConfig = async (appKey) => {
        setSelectedAppKey(appKey);
        try {
            const { data } = await getAppConfig(appKey);
            setConfigData(data);
            const firstTab = Object.keys(data)[0];
            setActiveTab(firstTab);
        } catch (err) {
            console.error('Konfigurationsfehler:', err);
        }
    };

    return (
        <div className="applications-layout">
            <div className="app-list">
                <h3>Applikationen</h3>
                <ul>
                    {apps.map(app => (
                        <li key={app.key}>
                            <button onClick={() => loadConfig(app.key)}>{app.name}</button>
                        </li>
                    ))}
                </ul>
            </div>

            <div className="app-details">
                {selectedAppKey && (
                    <>
                        <h2>Konfiguration: {selectedAppKey}</h2>
                        <div className="tabs">
                            {Object.keys(configData).map(configId => (
                                <button
                                    key={configId}
                                    className={`tab ${activeTab === configId ? 'active' : ''}`}
                                    onClick={() => setActiveTab(configId)}
                                >
                                    {configId}
                                </button>
                            ))}
                        </div>
                        <div className="tab-content">
                            <pre>
                                {activeTab && JSON.stringify(configData[activeTab], null, 2)}
                            </pre>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
}
