import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { useState } from 'react';
import CarouselMenu from './components/CarouselMenu';
import TileMenu from './components/TileMenu';
import Applications from './pages/Applications';
import Users from './pages/Users';
import Maintenance from './pages/Maintenance';
import SiteProperties from './pages/SiteProperties';
import Rive from './pages/InteractiveAnimation';
import './App.css';

function App() {
  const [useCarousel, setUseCarousel] = useState(true);

  const toggleMenu = () => {
    setUseCarousel(prev => !prev);
  };

  return (
    <Router>
      <div className="app-container">
        {/* Titel und Umschalter oben links */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
          <Link to="/" className="app-title">
            <h1>ApplicationManager</h1>
          </Link>
          <button onClick={toggleMenu}>
            {useCarousel ? 'Zur Kachel-Ansicht wechseln' : 'Zum Carousel wechseln'}
          </button>
        </div>

        <Routes>
          <Route
            path="/"
            element={
              <div className="menu-container">
                {useCarousel ? <CarouselMenu /> : <TileMenu />}
              </div>
            }
          />
          <Route path="/applications" element={<Applications />} />
          <Route path="/users" element={<Users />} />
          <Route path="/maintenance" element={<Maintenance />} />
          <Route path="/site-properties" element={<SiteProperties />} />
          <Route path="/rive" element={<Rive />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
