import React from 'react';
import './App.css';
import CarouselMenu from './components/CarouselMenu';

function App() {

  const StarBackground = () => {
    const stars = Array.from({ length: 100 }).map((_, i) => (
      <div
        key={i}
        className="star"
        style={{
          top: `${Math.random() * 100}%`,
          left: `${Math.random() * 100}%`,
          width: `${Math.random() * 3}px`,
          height: `${Math.random() * 3}px`,
          animationDelay: `${Math.random() * 3}s`
        }}
      />
    ));

    return <>{stars}</>;
  };

  return (
    <div className="App">
      {/* Header (optional) */}
      <header className="App-header">
        <h1>ApplicationManager</h1>
      </header>

      {/* Hauptinhalt mit Weltraum-Hintergrund */}
      <div className="main-content" style={styles.spaceBackground}>
        <StarBackground />
        <CarouselMenu />
      </div>
    </div>
  );
}

// Stile für den Weltraum-Effekt
const styles = {
  spaceBackground: {
    background: 'radial-gradient(ellipse at center, #0a0e24 0%, #000000 100%)',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white'
  }
};

export default App;
