import React, { useState, useEffect } from 'react';
import { getApplications, getAppConfig } from '../api';
import './Applications.css';

export default function Applications() {
    const [apps, setApps] = useState([]);
    const [config, setConfig] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        loadApplications();
    }, []);

    const loadApplications = async () => {
        setLoading(true);
        try {
            const { data } = await getApplications();
            setApps(data);
        } catch (err) {
            console.error('Fehler beim Laden:', err);
        } finally {
            setLoading(false);
        }
    };

    const loadConfig = async (appKey) => {
        try {
            const { data } = await getAppConfig(appKey);
            setConfig({ appKey, data });
        } catch (err) {
            console.error('Konfigurationsfehler:', err);
        }
    };

    return (
        <div className="applications-container">
            <h2>Applikationen verwalten</h2>

            {loading ? (
                <div className="loading">Lade Daten...</div>
            ) : (
                <table className="apps-table">
                    <thead>
                        <tr>
                            <th>App Key</th>
                            <th>Name</th>
                            <th>Schema</th>
                            <th>Status</th>
                            <th>Aktionen</th>
                        </tr>
                    </thead>
                    <tbody>
                        {apps.map(app => (
                            <tr key={app.id}>
                                <td>{app.key}</td>
                                <td>{app.name}</td>
                                <td>{app.schema}</td>
                                <td>
                                    <span className={`status ${app.active ? 'active' : 'inactive'}`}>
                                        {app.active ? 'Aktiv' : 'Inaktiv'}
                                    </span>
                                </td>
                                <td>
                                    <button
                                        className="config-btn"
                                        onClick={() => loadConfig(app.key)}
                                    >
                                        Konfig anzeigen
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            {config && (
                <div className="config-modal">
                    <h3>Konfiguration: {config.appKey}</h3>
                    <pre>{JSON.stringify(config.data, null, 2)}</pre>
                    <button onClick={() => setConfig(null)}>Schließen</button>
                </div>
            )}
        </div>
    );
}
