import React, { useState } from 'react';
import './CarouselMenu.css';

const menuItems = [
    { id: 1, title: 'Applikationen' },
    { id: 2, title: 'User' },
    { id: 3, title: 'Maintenance' },
    { id: 4, title: 'Site-properties' },
    { id: 5, title: 'Logs' }
];

const CarouselMenu = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    const handlePrev = () => {
        setActiveIndex((prev) => (prev === 0 ? menuItems.length - 1 : prev - 1));
    };

    const handleNext = () => {
        setActiveIndex((prev) => (prev === menuItems.length - 1 ? 0 : prev + 1));
    };

    return (
        <div className="carousel-container" style={{ gap: '100px' }}>
            {/* Pfeil-Links */}
            <button className="nav-arrow left-arrow" style={{ marginRight: 'auto' }} onClick={handlePrev}>
                ←
            </button>

            {/* Menü-Items mit elliptischem Layout */}
            <div className="carousel-track">
                {menuItems.map((item, index) => {
                    const position = (index - activeIndex + menuItems.length) % menuItems.length;
                    return (
                        <div
                            key={item.id}
                            className={`carousel-item ${position === 0 ? 'active' : ''}`}
                            style={{ '--pos': position }}
                            onClick={() => setActiveIndex(index)}
                        >
                            <div className="item-box">
                                {item.title}
                            </div>
                        </div>
                    );
                })}
            </div>

            {/* Pfeil-Rechts */}
            <button className="nav-arrow right-arrow" style={{ marginLeft: 'auto' }} onClick={handleNext}>
                →
            </button>
        </div>
    );
};

export default CarouselMenu;
