import React, { useState, useEffect } from 'react';
import { getApplications, getAppConfig } from '../api';
import './Applications.css';

export default function Applications() {
    const [apps, setApps] = useState([]);
    const [selectedApp, setSelectedApp] = useState(null);
    const [configData, setConfigData] = useState([]);
    const [selectedTab, setSelectedTab] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        loadApplications();
    }, []);

    const loadApplications = async () => {
        try {
            const { data } = await getApplications();
            setApps(data);
        } catch (err) {
            console.error('Fehler beim Laden der Anwendungen:', err);
        }
    };

    const handleAppClick = async (app) => {
        setSelectedApp(app);
        setLoading(true);
        setSelectedTab(null);
        try {
            const response = await getAppConfig(app.key);
            const config = Array.isArray(response.data) ? response.data : [];
            setConfigData(config);
            if (config.length > 0) {
                setSelectedTab(config[0].config_key);
            }
        } catch (err) {
            console.error('Fehler beim Laden der Konfiguration:', err);
            setConfigData([]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="applications-container">
            <div className="sidebar">
                <h3>Anwendungen</h3>
                {apps.map(app => (
                    <button
                        key={app.id}
                        className={`app-btn ${selectedApp?.key === app.key ? 'active' : ''}`}
                        onClick={() => handleAppClick(app)}
                    >
                        {app.name}
                    </button>
                ))}
            </div>

            <div className="main">
                {loading && <p>Lade Konfiguration...</p>}

                {!loading && selectedApp && (
                    <>
                        <h2>{selectedApp.name} – Konfiguration</h2>
                        <div className="tabs">
                            {configData.map(conf => (
                                <button
                                    key={conf.config_key}
                                    className={`tab-btn ${selectedTab === conf.config_key ? 'active' : ''}`}
                                    onClick={() => setSelectedTab(conf.config_key)}
                                >
                                    {conf.config_key}
                                </button>
                            ))}
                        </div>

                        <div className="config-details">
                            {configData
                                .filter(c => c.config_key === selectedTab)
                                .map(conf => (
                                    <div key={conf.id} className="config-entry">
                                        <div><strong>ID:</strong> {conf.id}</div>
                                        <div><strong>Config Key:</strong> {conf.config_key}</div>
                                        <div><strong>Config Value:</strong> {String(conf.config_value)}</div>
                                        <div><strong>Config Description:</strong> {String(conf.description)}</div>
                                        <div><strong>Zuletzt aktualisiert:</strong> {new Date(conf.updated_at).toLocaleString()}</div>
                                    </div>
                                ))}
                        </div>

                    </>
                )}
            </div>
        </div>
    );
}
